operation.add:
{% if useNomenclator == true %}
    <select name="city" id="input-shipping-city" class="form-control">
        <option>- Alege un judet -</option>
    </select>
{% else %}
    <input type="text" name="city" value="" placeholder="{{ entry_city }}" id="input-payment-city" class="form-control" />
{% endif %}

let SamedayCities = {{ sameday_cities }};
/**
 * @param fieldName
 *
 * @returns HTML|undefined
 */
const getFieldByName = (fieldName) => {
    return Array.from(document.querySelectorAll('input, select'))
        .find(element => element.id.includes(fieldName)
        );
}
let formElements = {
    country: $(getFieldByName('country')),
    zone: $(getFieldByName('zone')),
    city: $(getFieldByName('city')),
};

if (undefined !== formElements.zone && formElements.zone.length > 0) {
    formElements.zone.on('change', (event) => {
        updateCities(formElements.city[0], event.target.value, formElements.country.val());
    });
}

const updateCities = (cityField, zoneId, countryId) => {
    let cities = SamedayCities?.[countryId]?.[zoneId] ?? [];
    if (cities.length > 0) {
        if (undefined !== citySelectElement && citySelectElement.length > 0) {
            populateCityField(cities, citySelectElement, cityField);
        } else {
            citySelectElement = document.createElement("select");
            citySelectElement.setAttribute("id", cityField.getAttribute('id'));
            citySelectElement.setAttribute("name", 'city');
            citySelectElement.setAttribute("class", "form-control form-control-select");

            populateCityField(cities, citySelectElement, cityField);
        }
    } else {
        if (undefined !== citySelectElement && citySelectElement.length > 0) {
            citySelectElement.replaceWith(cityField);
        }
    }
}

const createOptionElement = (value, text, cityFieldValue = null) => {
    const option = document.createElement('option');
    option.value = value;
    option.setAttribute('data-alternate-values', `[${value}]`);
    if (value === cityFieldValue) {
        option.setAttribute('selected', true);
    }
    option.textContent = text;

    return option;
}

let citySelectElement;

const populateCityField = (cities, citySelectElement, cityField) => {
    citySelectElement.textContent = "";
    citySelectElement.appendChild(createOptionElement("", "Choose a city"));
    cities.forEach((city) => {
        citySelectElement.appendChild(createOptionElement(city.name, city.name, cityField.value));
    });

    cityField.replaceWith(citySelectElement);
}

public function get_cities() {

    $this->load->model('extension/shipping/sameday');
    $county_id = $this->request->post['county_id'];

    $cities = $this->model_extension_shipping_sameday->getCities($county_id);

    echo json_encode($cities);

}

public function getCountryByCode($isoCode){
    $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "country WHERE iso_code_2 = '$isoCode'");
    return $query->row['country_id'];
}

public function getCountiesByCountryId($country_id){
    $query = $this->db->query('SELECT * FROM ' . DB_PREFIX . "zone WHERE country_id = '" . (int)$country_id . "'");
    return $query->rows;
}

public function getCitiesByCountyId($zone_id){
    $query = $this->db->query('SELECT * FROM ' . DB_PREFIX . "sameday_cities WHERE zone_id = '" . (int)$zone_id . "'");
    return $query->rows;
}

$this->load->model('extension/shipping/sameday');
$data['useNomenclator'] = $this->model_extension_shipping_sameday->getConfig('sameday_nomenclator_use');

// Get Cities for Checkout
$countries = $this->model_extension_shipping_sameday->getCountriesCodes();
$sameday_cities = array();

foreach ($countries as $country) {
    $country_id = $this->getCountryByCode($country);
    $sameday_cities[$country_id] = array();
    $counties = $this->getCountiesByCountryId($country_id);
    foreach ($counties as $county) {
        $zone_id = $county['zone_id'];
        $sameday_cities[$country_id][$zone_id] = array();
        $cities = $this->getCitiesByCountyId($zone_id);
        foreach($cities as $city) {
            $sameday_cities[$country_id][$zone_id][] = array(
                'name' => $city['city_name']
            );
        }
    }
}

$data['sameday_cities'] = json_encode($sameday_cities);

