                $('#selectLocker').on('click', (event) => {
                    lockerSelector = event.target;
                    lockerValue = lockerSelector.value;

                    $('#locker_nextday').prop('checked', false);

                    if (lockerValue != "") {
                        $('#locker_nextday').prop('checked', true);
                    }

                    $('#locker_nextday').val(lockerValue);
                });
            });
