    {% if (quote.lockers is not empty) %}
        <div class="lockers">
            <div class="row">
                <ul>
                    <div class="col-sm-4">
                        <div id="search" class="input-group">
                            <select id="selectLocker" name="search" placeholder="Search" class="form-control input-lg">
                                <option class="input-group" value=""> Alege Locker </option>
